import React, { useState } from "react";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import { Button, IconButton } from "@mui/material";

export default function ImageCard({ item }) {
  const [items, setItems] = useState([])

  function AddItemToCard(e) {
    items.push(...items, e)
    // localStorage.setItem('products', JSON.stringify(e));
    if (localStorage.getItem('items') === null) {
      // Adicionando um array com um objeto no localstorage
      localStorage.setItem('items', JSON.stringify([dataObj]));
    } else {
      // Copiando o array existente no localstorage e adicionando o novo objeto ao final.
      localStorage.setItem(
        'items',
        // O JSON.parse transforma a string em JSON novamente, o inverso do JSON.strigify
        JSON.stringify([
          ...JSON.parse(localStorage.getItem('items')),
          e
        ])
      );
    }
  }
  console.log(items)
  return (
    <Card sx={{ width: "100%", borderRadius: 5 }}>
      <CardContent sx={{ textAlign: "center" }}>
        <CardMedia
          component="img"
          height="150"
          image={item.image}
          alt="Product Image"
        />
        <Typography   noWrap>
          {item.name}
        </Typography>
        <Typography sx={{ fontSize: 14 }} >
          R$ {item.price}
        </Typography>
      </CardContent>
      <CardActions sx={{ justifyContent: "center" }}>
        <Button
          variant="contained"
          color="success"
          sx={{ width: "25%", borderRadius: 10, backgroundColor: "#7FBA00" }}
          onClick={() => AddItemToCard(item)}
        >
          Comprar
        </Button>
      </CardActions>
    </Card>
  );
}
