import React, { useState } from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { Button } from "@mui/material";


export default function TableComponentCart() {
  const [count, setCount] = useState(0);
  const rows = [
    { Nome: "uva", Quantidade: 2, Valor: 12, Total: 24 }
  ];
  const header = ["Item", "Quantidade", "Total", ""];

  function increment(row) {
    setCount(prevCount => prevCount + 1);
  }

  function decrement(row) {
    setCount(prevCount => prevCount + 1);
  }


  return (
    <>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: "90vw" }}>
          <TableHead>
            <TableRow>
              {header.map((item) => (
                 <TableCell align="right">{item}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row, index) => (
              <TableRow key={row.id}>
                <TableCell align="right">{row.Nome}</TableCell>
                < TableCell align="right">
                  <Button variant="outlined" size="small" onClick={() => increment(row.Valor)}>
                    +
                  </Button>
                  {row.Valor}
                  <Button variant="outlined"  size="small" onClick={() => decrement(row.Valor)}>
                    -
                  </Button>
                </TableCell>
                <TableCell align="right">{row.Total}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer >
    </>
  );
}
