import React, { useEffect, useState } from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { Button } from "@mui/material";


export default function TableComponentCart() {
  const [rows, setRow] = useState([]);
  const [itemCount, setItemCount] = React.useState(1)
  const [items, setItems] = useState([])
  const header = ["Item", "Quantidade", "Total", ""];


  useEffect(() => {
    setRow(JSON.parse((localStorage.getItem('products'))))
  }, [])

  function updateCard(e) {

    let data = window.localStorage.getItem('products');
    if (data != null) {
      let cart = JSON.parse(data);
      for (let index = 0; index < array.length; index++) {
        cart[index].qtd = cart[index].qtd - 1;
        window.localStorage.setItem('products', JSON.stringify(cart));

      }

    }
  }

  return (
    <>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: "90vw" }}>
          <TableHead>
            <TableRow>
              {header.map((item, index) => (
                <TableCell key={index + 1} align="right">{item}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows?.map((row, index) => (
              <TableRow key={index + 1}>
                <TableCell align="right">{row.name}</TableCell>
                < TableCell align="right">
                  <Button variant="outlined" size="small" onClick={() => updateCard(row.qtd)}>
                    -
                  </Button>
                  {row.qtd}
                  <Button variant="outlined" size="small" onClick={() => {
                    setItemCount(row.qtd + 1);
                  }}>
                    +
                  </Button>
                </TableCell>
                <TableCell align="right">{itemCount * row.price}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer >
    </>
  );
}
