import React, { useEffect, useState } from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { Button } from "@mui/material";


export default function TableComponentCart() {
  const [rows, setRow] = useState([]);
  const [items, setItems] = useState(false)
  const header = ["Item", "Quantidade", "Total", ""];


  useEffect(() => {
    setItems(false)
    setRow(JSON.parse((localStorage.getItem('products'))))
  }, [items])

  function decrementCard(e) {
    setItems(true)
    let data = window.localStorage.getItem('products');
    if (data != null) {
      let cart = JSON.parse(data);
      for (let index = 0; index < cart.length; index++) {
        if (e.id === cart[index].id) {
          cart[index].qtd = cart[index].qtd - 1;
          window.localStorage.setItem('products', JSON.stringify(cart));
        }
      }

    }
  }
  function incrementCard(e) {
    setItems(true)
    let data = window.localStorage.getItem('products');
    if (data != null) {
      let cart = JSON.parse(data);
      for (let index = 0; index < cart.length; index++) {
        if (e.id === cart[index].id) {
          cart[index].qtd = cart[index].qtd + 1;
          window.localStorage.setItem('products', JSON.stringify(cart));
        }
      }

    }
  }
  function removeItem(e) {
    setItems(true)
    let data = window.localStorage.getItem('products');
    if (data != null) {
      let cart = JSON.parse(data);
      for (let index = 0; index < cart.length; index++) {
        if (e.id === cart[index].id) {
          cart.slice(index);
        }
      }
      localStorage.setItem('products', JSON.stringify([cart]));
    }

  }


return (
  <>
    <TableContainer  >
      <Table sx={{
        minWidth: "90vw",
        "& td, & th": { border: 0 }
      }} >
        <TableHead>
          <TableRow>
            {header.map((item, index) => (
              <TableCell key={index + 1} align="right">{item}</TableCell>
            ))}
          </TableRow>
        </TableHead>
        <TableBody>
          {rows?.length > 0 ? rows?.map((row, index) => (
            <TableRow key={index + 1} >
              <TableCell align="right">{row.name}</TableCell>
              < TableCell align="right">
                <Button variant="outlined" size="small" onClick={() => decrementCard(row)} sx={{ m: 1 }}>
                  -
                </Button>
                {row.qtd}
                <Button variant="outlined" size="small" onClick={() => incrementCard(row)} sx={{ m: 1 }}>
                  +
                </Button>
              </TableCell>
              <TableCell align="right">{row.qtd * row.price}</TableCell>
              <TableCell align="right">
                <Button variant="contained" size="small" color="error" onClick={() => removeItem(row)} sx={{ m: 1 }}>
                  remove
                </Button>
              </TableCell>
            </TableRow>
          )) : (<TableRow><TableCell align="center" colSpan={12}>Lista vazia</TableCell></TableRow>)}
        </TableBody>
      </Table>
    </TableContainer >
  </>
);
}
