import React, { useEffect, useState } from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { Button } from "@mui/material";


export default function TableComponentCart() {
  const [rows, setRow] = useState([]);
  const [itemCount, setItemCount] = React.useState(1)
  const [items, setItems] = useState([])
  const header = ["Item", "Quantidade", "Total", ""];


  useEffect(() => {
    setRow(JSON.parse((localStorage.getItem('products'))))
  }, [])

  function updateCard(e) {

    // localStorage.setItem('products', JSON.stringify(e));
    if (localStorage.getItem('products') === null) {
      // Adicionando um array com um objeto no localstorage
      localStorage.setItem('products', JSON.stringify([{
        id: e.id,
        name: e.name,
        price: e.price,
        qtd: e.qtd + 1,
        image: e.image
      },]));
    } else {
      // Copiando o array existente no localstorage e adicionando o novo objeto ao final.
      localStorage.setItem(
        'products',
        // O JSON.parse transforma a string em JSON novamente, o inverso do JSON.strigify
        JSON.stringify([
          ...JSON.parse(localStorage.getItem('products')),
          e
        ])
      );
    }
  }

  return (
    <>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: "90vw" }}>
          <TableHead>
            <TableRow>
              {header.map((item, index) => (
                <TableCell key={index + 1} align="right">{item}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows?.map((row, index) => (
              <TableRow key={index + 1}>
                <TableCell align="right">{row.name}</TableCell>
                < TableCell align="right">
                  <Button variant="outlined" size="small" onClick={() => updateCard(row.qtd)}>
                    -
                  </Button>
                  {row.qtd}
                  <Button variant="outlined" size="small" onClick={() => {
                    setItemCount(row.qtd + 1);
                  }}>
                    +
                  </Button>
                </TableCell>
                <TableCell align="right">{itemCount * row.price}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer >
    </>
  );
}
